import React from "react";
import "./../styles.scss";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from './../../../../actions/index'

class LoginFormRightLogin extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        email: "",
        password: "",
        checkLogin:false
    };
  }
  onSubmit=(e)=>{
    e.preventDefault();
    this.props.onLogin(this.state);
  }
  onChange=(e)=>{
    this.setState({
      [e.target.name]:e.target.value
    })
  }
  checklogin=()=>{
    if (this.props.checkLogin){
      this.setState({
        checkLogin:true
      })
    }

  }
  render() {

      var{email,password}=this.state
    return (
      <div className="content-right">
        <form onSubmit={this.onSubmit} className="form-info">
          <h1>LOGIN</h1>
          <div className="formField">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              className="form-input"
              placeholder="Enter your email"
              value={email}
              onChange={this.onChange}
            />
          </div>
          <div className="formField">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              className="form-input"
              placeholder="Enter your password"
              value={password}
              onChange={this.onChange}
            />
          </div>
          <div className="formField">
            {/* <button
                type="submit"
                id="btnlogin"
                name="btnlogin"
                className="btn-login"
              >
                LOGIN
              </button> */}
            <button type="submit" className="btn-login" onClick={this.checklogin}>
              LOGIN
            </button>
            <Link to="/signup" className="link">
              Create an account
            </Link>
            <div className="title-background-hidden">
              <p>Â© 2019 Terralogic, Inc</p>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
const mapDispatchToProps=(dispatch,props)=>{
  return{
      onLogin:(state)=>{
          dispatch(actions.login(state))
      }
  }
}
const mapStateToProps = state => {
  return {
    checkLogin: state.login
  };
};
export default connect(mapStateToProps,mapDispatchToProps)(LoginFormRightLogin);
