import React from "react";
import "./styles.scss";
import { Link } from "react-router-dom";
import axios from "axios";
import { connect } from "react-redux";
import * as actions from "./../../actions/index";
class HeaderForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  confirmLogout = () => {
    this.props.onLogout();
  };

  render() {
    return (
      <div className="row">
        <div className="p-header">
          <div className="col-sm-3">
            <img
              src={require("./../../images/terralogic.png")}
              className="p-picture-background"
              alt="img"
            />
          </div>
          <div className="col-sm-1 pull-right">
            <span className="dropdown-toggle p-link" data-toggle="dropdown">
              PROFILE
            </span>
            <ul className="dropdown-menu listItem">
              <li>
                <Link to="/profile">  <i class="fas fa-spinner fa-spin"></i>
Profile</Link>
              </li>
              <li>
                <Link to="/" onClick={this.confirmLogout}>Logout</Link>
              </li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch, props) => {
  return {
    onLogout: () => {
      dispatch(actions.userLogoutAPI());
    }
  };
};
const mapStateToProps = state => {
  return {
    profile: state.profile
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(HeaderForm);
