import * as types from "../actions/actionTypes";
import axios from "axios";
var initialState = {
  email: "",
  password: "",
  checkLogin: false
};
var myReducer = (state = initialState, action) => {
  switch (action.type) {
    case types.LOGIN:
      return {
        ...state,
        email: action.state.email,
        password: action.state.password,
        checkLogin: true
      };
    case types.SIGNUP:
      axios({
        method: "post",
        url: "https://terralogic-training.firebaseapp.com/api/login",
        data: action.state
      })
        .then(res => {
          console.log("res", res);
          return {
            ...state,
            email: action.state.email,
            password: action.state.password,
            checkLogin: true
          };
          // alert("Sign Up Successfully");
        })
        .catch(err => {
          console.log("error", err);
          alert("Email already exist");
        });
      return state;
    default:
      return state;
  }
};
export default myReducer;
