import React from "react";
import "./../styles.scss";
import { Link, NavLink } from "react-router-dom";
import { connect } from "react-redux";
import axios from "axios";
class LoginFormRightLogin extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        email: "",
        password: ""
    };
  }
  onSubmit=(e)=>{
    e.preventDefault();

  }
  onChange=(e)=>{
    this.setState({
      [e.target.name]:e.target.value
    })
  }
  render() {
    axios({
      method: "post",
      url: "https://terralogic-training.firebaseapp.com/api/sign_up"
    })
      .then(res => {
        console.log("response", res);
      })
      .catch(err => {
        console.log("error", err);
      });
      var{email,password}=this.state
    return (
      <div className="content-right">
        <form onSubmit={this.onSubmit} className="form-info">
          <h1>LOGIN</h1>
          <div className="formField">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              className="form-input"
              placeholder="Enter your email"
              value={email}
              onChange={this.onChange}
            />
          </div>
          <div className="formField">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              className="form-input"
              placeholder="Enter your password"
              value={password}
              onChange={this.onChange}
            />
          </div>
          <div className="formField">
            {/* <button
                type="submit"
                id="btnlogin"
                name="btnlogin"
                className="btn-login"
              >
                LOGIN
              </button> */}
            <button type="submit" className="btn-login">
              LOGIN
            </button>
            <Link to="/signup" className="link">
              Create an account
            </Link>
            <div className="title-background-hidden">
              <p>Â© 2019 Terralogic, Inc</p>
            </div>
          </div>
        </form>
      </div>
    );
  }
}

export default LoginFormRightLogin;
