import React from "react";
import "./../styles.scss";
import { connect } from "react-redux";
import * as actions from "./../../../../actions/index";
class ProfileForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      displayName:'',
      displayInfo:'',
      phone:'',
      avatar:''
    };
  }
  onSubmit = e => {
    e.preventDefault();
    console.log(this.state);
    this.props.onUpdateProfile(this.state);

    // this.setState({
    //   checkLogin:this.props.login.checkLogin===true?true:false
    // })
    // console.log(this.state);

  };
  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  componentDidMount(){
   this.props.onShowProfile()
  }
  render() {
    var {profile}=this.props
    return (
      <div className="p-content">
        <form className="p-box" onSubmit={this.onSubmit}>
          <div className="row m-b-120 p-imgField">
            <img
              src={require("./../../../../images/avatar-circle-human-female-black-7-512.png")}
              className="p-picture-avatar rounded"
              alt={profile.avatar}
            />
            <a href="/#" onClick="">
              <i className="fa fa-cog	" />
            </a>
          </div>
          <div className="row  ">
            <div className="col-sm-6">
              <div className="p-formField">
                <label>Display Name</label>
                <input
                  type="text"
                  id="displayName"
                  name="displayName"
                  className="p-form-input"
                  value={profile.display_name}
                  placeholder="Your name"

                />
              </div>
            </div>
            <div className="col-sm-6">
              <div className="p-formField">
                <label >Display Info</label>
                <input
                  type="text"
                  id="displayInfo"
                  name="displayInfo"
                  className="p-form-input"
                  value={profile.display_info}
                  placeholder="Your info"
                />
              </div>
            </div>
            <div className="col-sm-6">
              <div className="p-formField">
                <label >Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  className="p-form-input"

                  placeholder="Your email"
                />
              </div>
            </div>
            <div className="col-sm-6">
              <div className="p-formField">
                <label >Phone Number</label>
                <input
                  type="phone"
                  id="phone"
                  name="phone"
                  className="p-form-input"
                  value={profile.phone}
                  placeholder="Phone Number"
                />
              </div>
            </div>
            <div className="col-sm-6">
              <div className="p-formField">
                <label >New Password</label>
                <input
                  type="text"
                  id="password"
                  name="password"
                  className="p-form-input"
                  value=""
                  placeholder="Password"
                />
              </div>
            </div>
            <div className="col-sm-6">
              <div className="p-formField">
                <label >Confirm Password</label>
                <input
                  type="text"
                  id="confirm"
                  name="confirm"
                  className="p-form-input"
                  value=""
                  placeholder="Confirm password"
                />
              </div>
            </div>
          </div>
          <div className="row " >
            <button className="p-btn-form-submit" type="submit">
              Update profile
            </button>
          </div>
        </form>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch, props) => {
  return {
    onShowProfile:()  => {
      dispatch(actions.showProfileAPI());
    },
    onUpdateProfile:state  => {
      dispatch(actions.updateProfileAPI(state));
    }
  };
};
const mapStateToProps = state => {
  return {
    profile: state.profile
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProfileForm);
