import React from "react";
import "./../styles.scss";
import { Link, Redirect } from "react-router-dom";
import { connect } from "react-redux";
import * as actions from "./../../../../actions/index";
import axios from "axios";

class LoginFormRightLogin extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      checkLogin: false
    };
  }
  onSubmit = e => {
    e.preventDefault();
    var{email,password}=this.state;
    axios({
      method: "post",
      url: "https://terralogic-training.firebaseapp.com/api/login",
      data: {
        email:email,
        password:password
      }
    })
      .then(res => {
        console.log("res", res);
        this.props.onLogin(this.state);

      })

      .catch(err => {
        console.log("error", err);
      });

    // setTimeout(() => {
    //   console.log(this.props.checkLogin);

    // }, 1000);
    console.log(this.props);
  };
  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  render() {
    var { email, password } = this.state;
    // if (this.state.checkLogin) {
    //   return <Redirect to="/profile" />;
    // }
    return (
      <div className="content-right">
        <form onSubmit={this.onSubmit} className="form-info"  >
          <h1>LOGIN</h1>
          <div className="formField">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              className="form-input"
              placeholder="Enter your email"
              value={email}
              onChange={this.onChange}
            />
          </div>
          <div className="formField">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              className="form-input"
              placeholder="Enter your password"
              value={password}
              onChange={this.onChange}
            />
          </div>
          <div className="formField">
            {/* <button
                type="submit"
                id="btnlogin"
                name="btnlogin"
                className="btn-login"
              >
                LOGIN
              </button> */}
             <button type="submit" className="btn-login">
              LOGIN
            </button>
            <Link to="/signup" className="link">
              Create an account
            </Link>
            <div className="title-background-hidden">
              <p>Â© 2019 Terralogic, Inc</p>
            </div>
          </div>
        </form>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch, props) => {
  return {
    onLogin: state => {
      dispatch(actions.login(state));
    }
  };
};
const mapStateToProps = state => {
  return {
    login: state.login
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(LoginFormRightLogin);
