var initialState={
    email:'',
    password:''
}
var myReducer=(state=initialState,action)=>{
    switch (action.type) {
        case 'LOGIN':
            return{
                email:'hoang.nguyen@terralogic.com',
                password:'123'
            }

        default:
            break;
    }
}
export default myReducer;
