import * as types from "./actionTypes";
import axios from "axios";

export const login = state => {
  return {
    type: types.LOGIN,
    state
  };
};
export const signUp = state => {
  return {
    type: types.SIGNUP,
    state
  };
};
export const loginAPI = state => {
  return (dispatch) => {
    axios({
      method: "post",
      url: "https://terralogic-training.firebaseapp.com/api/login",
      data: state
    })
      .then(res => {
        state.token=res.data.token
        console.log(res);
        dispatch(login(state));
      })
      .catch(er => {
        alert("Login fail");

      });
  };
};
export const signUpAPI = state => {
  return (dispatch) => {
    axios({
      method: "post",
      url: "https://terralogic-training.firebaseapp.com/api/sign_up",
      data: state
    })
      .then(res => {
        console.log(res);
        dispatch(signUp(state));
      })
      .catch(er => {
        alert("Email is already exist");
      });
  };
};
export const showProfileAPI = state => {
  return (dispatch) => {
    axios({
      method: "post",
      url: "https://terralogic-training.firebaseapp.com/api/get_profile",
      Header:state.token
    })
      .then(res => {
        console.log(res);

      })
      .catch(er => {
        alert("err");
      });
  };
};
