import * as types from "../actions/actionTypes";
import axios from "axios";
var initialState = {
  checkLogin: false
};
var myReducer = (state = initialState, action) => {
  switch (action.type) {
    case types.LOGIN:
      axios({
        method: "post",
        url: "https://terralogic-training.firebaseapp.com/api/login",
        data: action.state
      })
        .then(res => {
          console.log("res", res);
          var newState = { ...state };
          newState.checkLogin = true;
          return {
            newState
          };
        })

        .catch(err => {
          console.log("error", err);
        });

      return state;

    case types.SIGNUP:
      axios({
        method: "post",
        url: "https://terralogic-training.firebaseapp.com/api/sign_up",
        data: action.state
      })
        .then(res => {
          console.log("res", res);
          alert("Sign Up Successfully");
        })
        .catch(err => {
          console.log("error", err);
          alert("Email already exist");
        });
      return state;
    default:
      return state;
  }
};
export default myReducer;
